import React, { useEffect, useState } from 'react' 
import "./Login.css"
//import { useNavigate ,Link} from 'react-router-dom'
//import { HomePage } from '../Pages/HomePage';
const Login = () => {

const[email , setEmail ]= useState(" ")

const [password ,setPassword] = useState([])

const handleUserName=(e)=>{
console.log(e.target.value)
setEmail(e.target.value)

}
const handlePassWord=(e)=>{
  setPassword(e.target.value)
  
  }

  const hanndleSubmit =(e)=>{
   e.preventDefault()
   console.log ("Submitted")
   axios.post(`http://localhost:8080/api/v1/auth/signin `,{

    email : email,
    password : password

})
.then((res)=>{
  console.log(res.data)  
  alert("Successfully LoggedIn") 

})
.catch((err)=>{
  console.log(err)
  console.log(err.res)

})

}




const handleLogin = () => {
  //API

  // usersList

  const userData = usersList.filter((user) => {
    return user.email === loginForm.email && user.password === loginForm.password
  });


  if (userData[0]) {
    localStorage.setItem("user", JSON.stringify(userData));
   navigate('/home')
  } else {
    alert("Username or password is incorrect");
  }
};

  return (

    
    <div class="mb-3">
      <h1>Login</h1>
  <form onSubmit={hanndleSubmit}>
    <label>Email</label>
    <input type='text' value={email} onChange={handleUserName}/>
    <label>Password</label>
    <input type='password' value={password} onChange={handlePassWord}/>
    <input type='submit' value="login"/>

  </form>
  <Link className=" link" to="/forgotpassword"><p>Forgot Password</p></Link>

</div>

  )
  
}
export default Login;
