password : " "

})
.then((res)=>{
  setUsersList(res.data);   

})
.catch((err)=>{
  console.log(err);
})

},[])

const handleLogin = () => {
  //API

  // usersList

  const userData = usersList.filter((user) => {
    return user.email === loginForm.email && user.password === loginForm.password
  });


  if (userData[0]) {
    localStorage.setItem("user", JSON.stringify(userData));
   navigate('/home')
  } else {
    alert("Username or password is incorrect");
  }
};

  return (

    
    <div class="mb-3">
      <h1>Login</h1>
  <label htmlFor="exampleFormControlInput1" className="form-label"></label>
  <input type="email"
   name='email' 
   value={loginForm.email}
  onChange={(e)=>setLoginForm({ ...loginForm, [e.target.name]:e.target.value})} 
  className="form-control"
   id="floatingInput" placeholder="email"/>



   
  <label htmlFor="exampleFormControlInput1" className="form-label"></label>
  <input type="password" 
  name='password'
   value={loginForm.password}   
   onChange={(e)=>setLoginForm({ ...loginForm,[e.target.name]:e.target.value})} 
   className="form-control"
    id="floatingPassword" 
    placeholder="password"/>
  <button className='btn btn-primary' onClick={handleLogin}>Login</button>
  <Link className=" link" to="/forgotpassword"><p>Forgot Password</p></Link>

</div>

  )
  
}
export default Login;
